    <script type="text/javascript">
      var pubilc_key = "-----BEGIN PUBLIC KEY----------END PUBLIC KEY-----";
      var private_key = "-----BEGIN PRIVATE KEY----------END PRIVATE KEY-----";
      function RSA_encryption(message, publicKey){
        var encrypt = new JSEncrypt();
        encrypt.setPublicKey(publicKey);
        var encrypted = encrypt.encrypt(message);
        return encrypted;
      }
      function RSA_decryption(ciphertext, privateKey){
        var decrypt = new JSEncrypt();
        decrypt.setPrivateKey(privateKey);
        var decrypted = decrypt.decrypt(ciphertext);
        return decrypted;
      }

      




    </script>

